package javaprogram;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		float b = 6.4f;
		System.out.println("a="+a);
		System.out.println("b="+b);
		System.out.println("a+b = "+(a+b));
		System.out.println("a-b = "+(a-b));
		System.out.println("a*b = "+(a*b));
		System.out.println("a/b = "+(a/b));
		System.out.println("a%b = "+(a%+b));
		
	}

}
